import React from "react";
import Home from './components/home';
import { Container, Row, Col } from 'reactstrap';
import { LocalStorage } from 'ttl-localstorage';

async function loggedIn () {
  const status = await LocalStorage.get('loggedIn');
  if(status === true) <Home />;
  else  <FormMarkup />;
}

// LocalStorage.timeoutInSeconds = 10800;
// LocalStorage.put('loggedIn', true);

function FormMarkup () {
  return (
    <div>
      <Container>
        <h1>Password behind login</h1>
      </Container>
    </div>
  );
}

export default class App extends React.Component {
  constructor(props) {
    super(props);
  }
  render() {
    return loggedIn();
  }
}
