import React from "react";
import { Container, Row, Col } from 'reactstrap';
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
export const UCA = () => {
    return (
        <div>
          <h2>Universal claim app</h2>
          <p className="details">Client: Elsevier <br />
          Role: Senior UX designer<br />
          Year: 2017/2018</p>
          <hr className="thick"/>
          <Row>
            <Col xs="8">
              <h3>About Elsevier</h3>
              <p>Elsevier is an information and analytics company and one of the 
                world's major providers of scientific, technical, and medical information. 
                Elsevier publishes more than 430,000 articles annually in 2,5000 journals. 
                Total yearly downloads amount to more than 900 million.</p>
            </Col>
            <Col xs="8">
              <h3>Challenge</h3>
              <p>Lorem Ipsum is simply dummy text of the printing and typesetting industry. 
                Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, 
                when an unknown printer took a galley of type and scrambled it to make a type 
                specimen book. It has survived not only five centuries, but also the leap into 
                electronic typesetting, remaining essentially unchanged. It was popularised in the 
                1960s with the release of Letraset sheets containing Lorem Ipsum passages, and more 
                recently with desktop publishing software like Aldus PageMaker including versions of 
                Lorem Ipsum.
              </p>
            </Col>
            <Col xs="8">
              <h3>Gathering information & planning</h3>
              <p>Lorem Ipsum is simply dummy text of the printing and typesetting industry. 
                Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, 
                when an unknown printer took a galley of type and scrambled it to make a type 
                specimen book.
              </p>
              <hr className="thick"/>
            </Col>
            <Col xs="12">
              <h3>User journeys</h3>
            </Col>
          </Row>
        </div>
    );
};

export const About = () => {
    return (
      <div>
        <h1>About</h1>
        <hr className="thick"/>
      </div>
    );
};

export const Topics = ({ match }) => {
    return (
      <div>
        <h1>Topics</h1>
        <hr className="thick"/>
        <ul>
          <li>
            <Link to={`${match.url}/rendering`}>Rendering with React</Link>
          </li>
          <li>
            <Link to={`${match.url}/components`}>Components</Link>
          </li>
          <li>
            <Link to={`${match.url}/props-v-state`}>Props v. State</Link>
          </li>
        </ul>

        <Route path={`${match.path}/:topicId`} component={Topic} />
        <Route
          exact
          path={match.path}
          render={() => <h3>Please select a topic.</h3>}
        />
      </div>
    );
};

export const Topic = ({ match }) => {
    return (
      <div>
        <h3>{match.params.topicId}</h3>
      </div>
    );
};
