import React from "react";
import { Container, Row, Col } from 'reactstrap';
import { CookiesProvider } from 'react-cookie';
import Home from './components/home';

export default class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {value: ''};
    this.handleChange = this.handleChange.bind(this);
    this.loggedIn = this.loggedIn.bind(this);
    this.formMarkup = this.formMarkup.bind(this);
  }

  handleChange(event) {
    this.setState({value: event.target.value});
  }

  loggedIn () {
    const { cookies } = this.props;
    const loggedInCookie = cookies.get('loggedIn');
    if(loggedInCookie === true) {
      return true;
    } else {
      return false;
    }
  }

  formMarkup () {
    const onSubmit = (event) => {
      event.preventDefault();
      console.log('sup', this.state.value);
    };

    return (
      <div>
        <Container>
          <h1>Password behind login</h1>
          <form onSubmit={onSubmit}>
          <label>
            Password:
            <input type="text" name="password" value={this.state.value} onChange={this.handleChange} />
          </label>
          <input type="submit" value="Submit" />
        </form>
        </Container>
      </div>
    );
  }

  render() {
    return (
    <CookiesProvider>
      {this.loggedIn() ? <Home /> : this.formMarkup() }
    </CookiesProvider>
    );
  }
}
